mapper.select:
select id,
name,
code,
unit,
state,
grade,
estate,
heating,
reason
from cims_person


        name like concat('%',#{name},'%')


        and code=#{code}


        and state=#{state}


        and grade=#{grade}


        and heating=#{heating}


        and estate=#{estate}


        and heating=1


        and estate=1


limit #{startSize},#{pageSize}

select count(1) from cims_person


        name like concat('%',#{name},'%')


        and code=#{code}


        and state=#{state}


        and grade=#{grade}


        and heating=1


        and estate=1
